import { useContext, useState } from 'react';
import { AuthContext } from './AuthContext';


function CreateLeague () {

    const [leagueDetials, setLeagueDetails] = useState({league_name: '', created_by: ''})

    const { userDetails } = useContext(AuthContext);


    const handleChange = (event) => {
        event.preventDefault();
        setLeagueDetails((prevDetails) => ({
            ...prevDetails,
            league_name: event.target.value, created_by: userDetails.id}))
    }

    const createNewLeague = async () => {
        const response = await fetch('http://localhost:5010/profile/create-league');
        const data = response.json();

        console.log(data);
    }

    return(
        <div>
            <form onSubmit={createNewLeague}>
                <h3>Create a new League</h3>
                <p>Enter team name and click "Create League" to create a new league</p>
                <input name="leagueName" onChange={handleChange} type="text"></input>
                <button>Create League</button>
            </form>

        </div>
    )
}


export default CreateLeague;
