import { useState, useEffect, useContext} from 'react';
import { AuthContext } from '../components/AuthContext';


function Leaderboard (){

    const [leagues, setLeagues] = useState([]);
    const [selectedLeague, setSelectedLeague] = useState();

    const { userDetails } = useContext(AuthContext);


    const fetchLeagues = async () => {
    const leaguesQuery = await fetch(`http://localhost:5010/profile/display-leagues?userId=${userDetails.id}`) 
    const playerLeagues = await leaguesQuery.json();

        console.log(playerLeagues);
        setLeagues(playerLeagues);
    }


    useEffect(() => {
        fetchLeagues()
    }, [userDetails.id])

    const handleLeagueChange = (event) => {
        setSelectedLeague(event.target.value)
    }

    const fetchLeagueData = async () => {
        const leagueDataQuery = await fetch(`http://localhost:5010/data/scores?leagueId=${selectedLeague}`);
        const scores = await leagueDataQuery.json();
        console.log(scores)
    }

    useEffect(() =>{
            fetchLeagueData()
        },[selectedLeague])

    console.log(selectedLeague);

    return(
        <div>
            <h1>LEADERBOARD</h1>
            <div>
                <select onChange={handleLeagueChange}>
                    <option value='' >--Select a league--</option>
                    {leagues.map((league) => <option key={league.index} value={league.id}>{league.league_name}</option>)}
                </select>
            </div>
        </div>
    )
}

export default Leaderboard;
