import { useContext, useState, useEffect } from "react";
import { AuthContext } from "./AuthContext";


function JoinLeague(){

    const [joinDetails, setJoinDetails] = useState({leagueId: '', userId: ''})
    const [joinLeagueFailed, setJoinLeaguefailed] = useState(false);
    const [responseMessage, setResponseMessage] = useState('');
    const [joinSuccess, setJoinSuccess] = useState(false);

    const { userDetails } = useContext(AuthContext);

    useEffect(() => {
        setJoinDetails((prevDetails) => ({
            ...prevDetails,
            userId: userDetails.id
        }))
    }, [userDetails]);

    const handleChange = (event) => {
        setJoinDetails((prevDetails) => ({
            ...prevDetails,
            [event.target.name]: event.target.value 
        }))
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        const response = await fetch('http://localhost:5010/profile/join-league', {
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(
                joinDetails
            )
        });

        const data = await response.json();

        if(!response.ok){
            setJoinLeaguefailed(true);
            setResponseMessage(data.message);
        } else if(response.ok){
            setJoinSuccess(true);
            setResponseMessage(data.message);
        }
    }


    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>Enter the ID of the League you would like to join:</label>
                <input onChange={handleChange} className="league-id" type="text" />
                <button>Join League</button>
            </form>
            <div>
                {joinSuccess ? <p>{responseMessage}</p> : null}
                {joinLeagueFailed ? <p>{responseMessage}</p> : null}
            </div>
        </div>
    )
}

export default JoinLeague;
