import { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../components/AuthContext';


function Submit(){

    const [submissionDetails, setSubmissionDetails] = useState({});
    const [weeks, setWeeks] = useState([
        '4/1/2025 - 10/1/2025',
        '11/1/2025 - 17/1/2025',
        '18/1/2025 - 24/1/2025',
        '25/1/2025 - 31/1/2025',
        '1/2/2025 - 7/2/2025',
        '8/2/2025 - 14/2/2025',
        '15/2/2025 - 21/2/2025',
        '22/2/2025 - 28/2/2025',
        '1/3/2025 - 7/3/2025',
        '8/3/2025 - 14/3/2025',
        '15/3/2025 - 21/3/2025',
        '22/3/2025 - 28/3/2025',
        '29/3/2025 - 4/4/2025',
        '5/4/2025 - 11/4/2025',
        '12/4/2025 - 18/4/2025',
        '19/4/2025 - 25/4/2025',
        '26/4/2025 - 2/5/2025',
        '3/5/2025 - 9/5/2025',
        '10/5/2025 - 16/5/2025',
        '17/5/2025 - 23/5/2025',
        '24/5/2025 - 30/5/2025',
        '31/5/2025 - 6/6/2025',
        '7/6/2025 - 13/6/2025',
        '14/6/2025 - 20/6/2025',
        '21/6/2025 - 27/6/2025',
        '28/6/2025 - 4/7/2025',
        '5/7/2025 - 11/7/2025',
        '12/7/2025 - 18/7/2025',
        '19/7/2025 - 25/7/2025',
        '26/7/2025 - 1/8/2025',
        '2/8/2025 - 8/8/2025',
        '9/8/2025 - 15/8/2025',
        '16/8/2025 - 22/8/2025',
        '23/8/2025 - 29/8/2025',
        '30/8/2025 - 5/9/2025',
        '6/9/2025 - 12/9/2025',
        '13/9/2025 - 19/9/2025',
        '20/9/2025 - 26/9/2025',
        '27/9/2025 - 3/10/2025',
        '4/10/2025 - 10/10/2025',
        '11/10/2025 - 17/10/2025',
        '18/10/2025 - 24/10/2025',
        '25/10/2025 - 31/10/2025',
        '1/11/2025 - 7/11/2025',
        '8/11/2025 - 14/11/2025',
        '15/11/2025 - 21/11/2025',
        '22/11/2025 - 28/11/2025',
        '29/11/2025 - 5/12/2025',
        '6/12/2025 - 12/12/2025',
        '13/12/2025 - 19/12/2025',
        '20/12/2025 - 26/12/2025',
        '27/12/2025 - 2/1/2026'
    ]);

    const [currentWeek, setCurrentWeek] = useState('');

    const { userDetails } = useContext(AuthContext);

    useEffect(() => {
        if(userDetails){
            setSubmissionDetails((prevDetails)=> ({
                ...prevDetails,
                userId: userDetails.id
            }))
        }

    },[userDetails])

    const parseDate = (dateString) => {
        return new Date(dateString);
    }

    const findCurrentWeek = () => {
        const currentDate = new Date();
        for (let week of weeks){
            const [startDateString, endDateString] = week.split(' - ');

            const startDate = parseDate(startDateString);
            const endDate = parseDate(endDateString);

            if (currentDate >= startDate && currentDate <= endDate){
                return week;
            } else {
                return '';
            }
        }
    }

    useEffect(() => {
        const period = findCurrentWeek();
        setCurrentWeek(period);
    })

    const handleChange = (event) => {
        setSubmissionDetails((prevDetails) => ({
            ...prevDetails,
            [event.target.name]: event.target.value
        }));
    }

    return(
        <div>
            <h1>SUBMIT</h1>
            <form>
                <p>Submission for quiz week {currentWeek}</p>
                <label htmlFor="score">Score</label>
                <input  type="text" 
                        onChange={handleChange}
                        value={submissionDetails.score}
                        id="score"
                        name="score">
                </input>
                <label htmlFor='players'>Number of team members</label>
                <input  type="text" 
                        value={submissionDetails.players}
                        onChange={handleChange}
                        id="players"
                        name='players'
                        ></input>
            </form>
        </div>
    )
}

export default Submit;
