import { useContext, useState, useEffect } from 'react';
import { AuthContext } from './AuthContext';


function CreateLeague () {

    const [leagueDetails, setLeagueDetails] = useState({leagueName: '', createdBy: ''})
    const [leagueID, setLeagueID] = useState(null);
    const [creationfailed, setCreationFailed] = useState(false);
    const [leagueFailMessage, setLeagueFailMessage] = useState('')

    const { userDetails } = useContext(AuthContext);

    useEffect(() => {
        if(userDetails.id){
            setLeagueDetails((prevDetails) => ({
                ...prevDetails, createdBy: userDetails.id
                })
            )
        }
    }, [userDetails])


    const handleChange = (event) => {
        setLeagueDetails((prevDetails) => ({
            ...prevDetails,
            [event.target.name]: event.target.value}))
    }

    console.log(leagueDetails);

    const createNewLeague = async (event) => {
        event.preventDefault();
        const response = await fetch('http://localhost:5010/profile/create-league', {
            method: 'POST',
            headers: {'Content-Type': 'application/json' },
            body: JSON.stringify(
                leagueDetails
            )
        });

        const data = await response.json();

        if(!response.ok){
            setLeagueFailMessage(data.message)
            setCreationFailed(true);
        }
        else if(response.ok){
            setLeagueID(data.leagueID)
            setCreationFailed(false)
        }

        console.log(data);
    }

    return(
        <div className=''>
            <form onSubmit={createNewLeague}>
                <h3>Create a new League</h3>
                <p>Enter team name and click "Create League" to create a new league</p>
                <input name="leagueName" onChange={handleChange} type="text" value={leagueDetails.leagueName}></input>
                <button>Create League</button>
            </form>
            <div>
                {creationfailed ? <div><p>{leagueFailMessage}</p></div> : null}
                {leagueID ? <div><p>LEAGUE CREATED! Your unique league ID is: {leagueID}</p></div> : null}
            </div>
        </div>
    )
}


export default CreateLeague;
