import { useContext, useState, useEffect } from "react";
import { AuthContext } from "./AuthContext";


function JoinLeague(){

    const [joinDetails, setJoinDetails] = useState({leagueId: '', userId: ''}) 

    const { userDetails } = useContext(AuthContext);

    useEffect(() => {
        setJoinDetails((prevDetails) => ({
            ...prevDetails,
            userId: userDetails.id
        }))
    }, [userDetails])

    const handleChange = (event) => {
        setJoinDetails((prevDetails) => ({
            ...prevDetails,
            [event.target.name]: event.target.value 
        }))
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        const response = await fetch('http://localhost:5010/profile/join-league', {
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(
                joinDetails
            )
        });

        const data = await response.json();


    }


    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>Enter the ID of the League you would like to join:</label>
                <input onChange={handleChange} className="league-id" type="text" />
                <button>Join League</button>
            </form>
        </div>
    )
}

export default JoinLeague;
