import homeLogo from '../assets/images/home-image.jpg';
import React, { useState } from 'react';
import { useNavigate, Link} from 'react-router-dom';

function Register (){

    const [formData, setFormData] = useState({email: '', password: ''});
    const [loginFailed, setLoginFailed] = useState(false);
    const [responseMessage, setResponseMessage] = useState('');
    const [registrationOk, setRegistrationOk] = useState(false);
    const [registrationMessage, setRegistrationMessage] = useState('');

    const navigate = useNavigate();


    const handleChange = (event) => {
        setFormData((prevData) =>({
            ...prevData,
            [event.target.name]: event.target.value
            }));
        }

        console.log(formData);

    const handleSubmit = async (event) => {
        event.preventDefault();

        const response = await fetch('http://localhost:5010/users/register', {
            method: 'POST',
            headers: {'Content-Type': 'application/json' },
            body: JSON.stringify(
                formData)
        })

        const data = await response.json();


        if (!response.ok){
            setResponseMessage(data.message)
            setLoginFailed(true);
        } else if (response.ok){
            console.log(data);
            setRegistrationOk(true);
            setRegistrationMessage(data.message);
            setTimeout(() => {
                navigate('/home')
            }, 3000);
            ;
        }
    }

    return(
        <div className="page-container">
            <div className="register-image-container">
                <img src={homeLogo} alt="The Good Weekend Quiz Logo" />
            </div>
            <div className='register-container'>
                <form onSubmit={handleSubmit}>
                <h1>Register</h1>
                <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="Name">
                </input>
                <input 
                    type="text"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder='Email'
                >
                </input>
                <input 
                    type="text"
                    name="password"
                    value={formData.password}
                    placeholder='Password' 
                    onChange={handleChange}
                ></input>
                {registrationOk ? <div className='registration-ok'><p>{registrationMessage}</p></div> : null}
                {loginFailed ? <div className="failed-registration"><p>{responseMessage}</p></div> : null}
                <button className='register-button' type='submit'>Register</button>
                <p className="link-to-signin">Already have an account? Please <Link to='/login'>sign.</Link></p>
                </form>
            </div>
        </div>
    )
}

export default Register;
