import { useState, useEffect, useContext } from "react";
import { AuthContext } from "./AuthContext";


function LeaguesDisplay() {

    const [leagueDetails, setLeagueDetials] = useState({userId: '', leagueName: [], leagueId: [], numberOfPlayers: []});

    const { userDetails } = useContext(AuthContext)

    const fetchLeagues = async () => {

        const response = await fetch(`http://localhost:5010/profile/display-leagues?userId=${userDetails.id}`);
        const data = await response.json();
        setLeagueDetials((prevDetails) => ({
                    ...prevDetails,
                    userId: userDetails.id, leagueName: [data.league_name], leagueId: [data.league_id] 
                }));


    }

    useEffect(() => {})




    return(
        <div className='leagues-container'>
            <table>
                <thead>
                    <tc>League Name</tc>
                    <tc>League ID</tc>
                    <tc>Number of Players</tc>
                </thead>
                <tbody>
                    {leagueDetails.map((league) => {
                       return   <><tr>{league.leagueName}</tr>
                                <tr>{league.leagueId}</tr></>
                    })}
                </tbody>
            </table>
            {}
        </div>
    )
}

export default LeaguesDisplay;
