import homeLogo from '../assets/images/home-image.jpg';
import React, { useState, useContext } from 'react';
import { useNavigate, Link} from 'react-router-dom';
import { AuthContext } from '../components/AuthContext';

function Home (){

    const [formData, setFormData] = useState({name: '', email: '', password: ''});
    const [loginFailed, setLoginFailed] = useState(false);
    const [responseMessage, setResponseMessage] = useState('');

    const navigate = useNavigate();
    const { login } = useContext(AuthContext);


    const handleChange = (event) => {
        setFormData((prevData) =>({
            ...prevData,
            [event.target.name]: event.target.value
            }));
        }

        console.log(formData);

    const handleSubmit = async (event) => {
        event.preventDefault();

        const response = await fetch('http://localhost:5010/users/login', {
            method: 'POST',
            headers: {'Content-Type': 'application/json' },
            body: JSON.stringify(
                formData)
        })

        const data = await response.json();
        console.log(data);
        const user = {
            name: data.name,
            email: data.email,
            id: data.id
        }
        const token = data.token;


        if (!response.ok){
            setResponseMessage(data.message)
            setLoginFailed(true);
        } else if (response.ok){
            console.log(data);
            login(token, user);
            navigate('/leaderboard');
        }
    }


    return(
        <div className="page-container">
            <div className="home-image-container">
                <img src={homeLogo} alt="The Good Weekend Quiz Logo" />
            </div>
            <div className='login-container'>
                <form onSubmit={handleSubmit}>
                <h1>Login</h1>
                <input 
                    type="text"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder='Email'
                >
                </input>
                <input 
                    type="text"
                    name="password"
                    value={formData.password}
                    placeholder='Password' 
                    onChange={handleChange}
                ></input>
                {loginFailed ? <div className="failed-login"><p>{responseMessage}</p></div> : null}
                <button className='login-button' type='submit'>Login</button>
                <p className='link-to-register'>Don't have an account? Please <Link to='/register'>register.</Link></p>
                </form>
            </div>
        </div>
    )
}

export default Home;
